mapper.select:
select

from org_function



		APP_ID = #{appId,jdbcType=BIGINT}


		AND NAME = #{name,jdbcType=VARCHAR}


		AND CODE = #{code,jdbcType=VARCHAR}


		AND URL = #{url,jdbcType=VARCHAR}


		AND TYPE = #{type,jdbcType=INTEGER}


		AND PARENT_FUN_ID = #{parentFunId,jdbcType=BIGINT}


		AND CREATE_TIME = #{createTime,jdbcType=TIMESTAMP}


		AND UPDATE_TIME = #{updateTime,jdbcType=TIMESTAMP}


		AND ICON = #{icon,jdbcType=VARCHAR}


		AND PATH = #{path,jdbcType=VARCHAR}


		AND IS_SHOW = #{isShow,jdbcType=SMALLINT}


		AND IS_CHECK = #{isCheck,jdbcType=SMALLINT}


		AND IS_CONTROL = #{isControl,jdbcType=SMALLINT}


		AND SHORTCUT = #{shortcut,jdbcType=SMALLINT}


		AND SHORTCUT_ICON = #{shortcutIcon,jdbcType=VARCHAR}


		AND LEVEL = #{level,jdbcType=INTEGER}


		AND IS_DELETED = #{isDeleted,jdbcType=SMALLINT}
